INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (851, '716555', '褒禅山', NULL, '安徽 > 巢湖 > 巢湖景点 > 褒禅山', '巢湖', '巢湖', 'https://img1.qunarzz.com/travel/poi/1409/09/8cae96ded022ddffffffffffc8d65eac.jpg;https://img1.qunarzz.com/travel/poi/1707/3f/60ae6fd03210a037.jpg;https://img1.qunarzz.com/travel/poi/1707/5b/af5de5b07f289737.jpg;https://img1.qunarzz.com/travel/poi/1707/76/a74f601c75d26537.jpg;https://img1.qunarzz.com/travel/poi/1707/16/61ce0e2a5fd31337.jpg', '--', '巢湖景点排名第8', NULL, '0', '31.7962796118', '118.0490507973', '2', '褒禅山位于含山县城东北处7.5公里，旧名花山。唐贞观年间，高僧慧褒禅师结庐山下，死后葬此，其弟子改花山为褒禅山。褒禅山主要山峦有三座。东为灵芝山，树木参天，古以盛产木灵芝得名;中为起云峰，高耸挺拔，\"天欲雨，山则云遮雾障\"；西有碗儿岭，相传一罗汉出生于此，饮食后投碗于岭上，至今仍见坐卧痕迹。 褒禅山有华阳仙洞、褒禅烟雨的传说，“华阳仙洞”，传说仙翁往来此间，故名。华阳洞有天、碑、门、地四洞，其中碑洞洞深形特，洞内历代游客题记遗迹颇多。“华阳”两字系明代万历癸丑岁(公元1613年)海阳范惟美书。 登褒禅山极目远眺，四周青峰环绕，山野之趣，赏玩之乐，探幽之险，登临之兴，令人流连忘返。', '安徽巢湖市含山县城东北处7.5公里', NULL, NULL, '8:00-17:00', 15852, '四季皆宜', NULL, NULL, NULL, 'http://travel.qunar.com/p-oi716555-baoshanshan', '2', '5', '1', '5', '', '4', 9, 44815, 166, 41101, 183134, 13210, 335570, NULL, 161233, 41844, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
